mapper.select:
/*(equipMapper.getEquipList) */ SELECT a.equip_mast_no --설비번호 , a.equip_nm --설비자재명 , a.ware_cd --제품코드 , a.purs_unit_prc --구매단가 , a.make_cmpy_nm --제조사 , b.code_nm as madein_nm --원산지 , a.ware_spec --제품사 , a.file_no --이미지관리번호 , c.org_file_nm --원본파일 FROM equip_mast a JOIN code_info b on a.madein = b.code_no left join file_info c on a.file_no = c.file_no and a.cust_no = c.cust_no and c.used_yn ='Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
AND upper(replace(a.ware_cd,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
AND upper(replace(a.make_cmpy_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

SELECT COUNT(a.equip_mast_no) FROM ( SELECT a.equip_mast_no --센서번호 FROM equip_mast a JOIN code_info b on a.madein = b.code_no left join file_info c on a.file_no = c.file_no and a.cust_no = c.cust_no and c.used_yn ='Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
AND upper(replace(a.ware_cd,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
AND upper(replace(a.make_cmpy_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
) a

/*(equipMapper.getEquipDeviceList) */ SELECT a.equip_no --설비번호 , a.equip_nm --설비자재명 , c.mobl_no -- 테블릿번호 , c.device_no --장비번호 , c.device_ip --통신 ip , c.device_port --통신Potr , d.sys_menu_nm --작동메뉴명 FROM equip_info a join mobile_info c on a.equip_no = c.device_no and a.cust_no = c.cust_no and c.used_yn = 'Y' join sys_menu d on c.sys_menu_no = d.sys_menu_no and c.cust_no = d.cust_no and d.used_yn = 'Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

select count(a.equip_no) from ( SELECT a.equip_no --설비번호 FROM equip_info a join mobile_info c on a.equip_no = c.device_no and a.cust_no = c.cust_no and c.used_yn = 'Y' join sys_menu d on c.sys_menu_no = d.sys_menu_no and c.cust_no = d.cust_no and d.used_yn = 'Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
) a

mapper.update:
/*(equipMapper.dropEquip) */
UPDATE equip_info SET used_yn = 'N' ,mod_dt = NOW(), mod_id = #{userId}, mod_ip = #{ipaddr}
WHERE equip_no = #{equipNo}
and cust_no = #{custNo}

/*(equipMapper.revervalEquip) */
UPDATE equip_info SET used_yn = 'Y',mod_dt = NOW(), mod_id = #{userId}, mod_ip = #{ipaddr}
WHERE equip_no = #{equipNo}
  and cust_no = #{custNo}

mapper.select:
/*(equipMapper.getEquipRunStatList) */ SELECT a.equip_no --설비번호 , a.equip_nm --설비자재명 , c.mobl_no --장치관리번호 , c.device_ip --장치ip , c.device_port --장치port , e.prod_no --생산품목번호 , f.prod_nm --생산품목명 , substring(d.fr_hm,1,2) || ':' || substring(d.fr_hm,3,2) as fr_hm--시작시간 , substring(d.to_hm,1,2) || ':' || substring(d.to_hm,3,2) as to_hm--종료시간 , coalesce (case when e.max_make_wgt = 0 then 0 else (e.indc_wgt::numeric / e.max_make_wgt) * 100 end ,0) as oper_rt --기동율 FROM equip_info a join mobile_info c on a.equip_no = c.equip_no and a.cust_no = c.cust_no and c.used_yn = 'Y' left join oper_mast d on a.equip_no = d.equip_no and a.cust_no = d.cust_no and d.used_yn = 'Y' and to_char(now(),'YYYYMMDD') = to_char(d.make_dt,'YYYYMMDD') left join make_indc e on d.indc_no = e.indc_no and a.cust_no = e.cust_no and e.used_yn = 'Y' left join prod_info f on e.prod_no = f.prod_no and a.cust_no = f.cust_no and f.used_yn = 'Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

select count(a.equip_no) from ( SELECT a.equip_no --설비번호 FROM equip_info a join mobile_info c on a.equip_no = c.equip_no and a.cust_no = c.cust_no and c.used_yn = 'Y' left join oper_mast d on a.equip_no = d.equip_no and a.cust_no = d.cust_no and d.used_yn = 'Y' and to_char(now(),'YYYYMMDD') = to_char(d.make_dt,'YYYYMMDD') left join make_indc e on d.indc_no = e.indc_no and a.cust_no = e.cust_no and e.used_yn = 'Y' left join prod_info f on e.prod_no = f.prod_no and a.cust_no = f.cust_no and f.used_yn = 'Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
) a

/*(equipMapper.getEquipRunHstrList) */ SELECT a.equip_no --설비번호 , a.equip_nm --설비자재명 , c.mobl_no --장치관리번호 , c.device_ip --장치ip , c.device_port --장치port , e.prod_no --생산품목번호 , f.prod_nm --생산품목명 , substring(d.fr_hm,1,2) || ':' || substring(d.fr_hm,3,2) as fr_hm--시작시간 , substring(d.to_hm,1,2) || ':' || substring(d.to_hm,3,2) as to_hm--종료시간 , coalesce (case when e.max_make_wgt = 0 then 0 else (e.indc_wgt::numeric / e.max_make_wgt) * 100 end ,0) as oper_rt --기동율 FROM equip_info a join mobile_info c on a.equip_no = c.equip_no and a.cust_no = c.cust_no and c.used_yn = 'Y' join oper_mast d on a.equip_no = d.equip_no and a.cust_no = d.cust_no and d.used_yn ='Y' join make_indc e on d.indc_no = e.indc_no and a.cust_no = e.cust_no and e.used_yn ='Y' join prod_info f on e.prod_no = f.prod_no and a.cust_no = f.cust_no and f.used_yn = 'Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo} and to_char(d.make_dt,'YYYYMMDD') between #{dateFr} and #{dateTo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

select count(a.equip_no) from ( SELECT a.equip_no --설비번호 FROM equip_info a join mobile_info c on a.equip_no = c.equip_no and a.cust_no = c.cust_no and c.used_yn = 'Y' join oper_mast d on a.equip_no = d.equip_no and a.cust_no = d.cust_no and d.used_yn ='Y' join make_indc e on d.indc_no = e.indc_no and a.cust_no = e.cust_no and e.used_yn ='Y' join prod_info f on e.prod_no = f.prod_no and a.cust_no = f.cust_no and f.used_yn = 'Y' WHERE a.used_yn = 'Y' and a.cust_no = #{custNo} and to_char(d.make_dt,'YYYYMMDD') between #{dateFr} and #{dateTo}
AND upper(replace(a.equip_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
) a

/* (equipMapper.getEquipMngrItem)*/
select * from equip_mngr_item
where cust_no = #{custNo}
and equip_no = #{equipNo}
and used_yn = 'Y'

        /* (equipMapper.getEquipMngrHstr)*/
         select a.min_lmt_val --—최소값
              , a.max_lmt_val --—최대값
              , a.meas_val --—측정값
              , c.spot_nm --—측정장
              , to_char(to_timestamp(a.unix_hms),'HH24:MI') as meas_dt -- —측정시
         from   equip_mngr_hstr a join spot_equip b on a.spot_equip_no = b.spot_equip_no and a.cust_no = b.cust_no and b.used_yn = 'Y'
                                  join spot_info  c on b.spot_no = c.spot_no and a.cust_no = c.cust_no and c.used_yn ='Y'
		where  a.used_yn = 'Y'
        and a.cust_no = #{custNo}
        and c.spot_no = #{spotNo}
        and c.cust_no = #{custNo}
        and b.equip_no = #{equipNo}
        and to_char(to_timestamp(a.unix_hms),'YYYY-MM-DD') = to_char(now(),'YYYY-MM-DD')
        order by a.unix_hms desc limit #{listSz}

        /* (equipMapper.getEquipMngrHstrDoubleBack)*/
        SELECT a.meas_val AS 온도
		     , b.meas_val AS 습도
		     , a.spot_nm || b.spot_nm AS spot_nm
        FROM
			( SELECT b.meas_val
			        ,d.spot_nm
		   	FROM ( SELECT MAX(a.mngr_hstr_no) AS mngr_hstr_no
			       FROM equip_mngr_hstr a JOIN spot_equip b ON a.spot_equip_no = b.spot_equip_no AND b.spot_no = #{Num1}
			                                               and a.cust_no = b.cust_no and b.used_yn ='Y'
			                              JOIN spot_info C ON b.spot_no = c.spot_no and a.cust_no = c.cusst_no and c.used_yn ='Y'
		   	       where a.cust_no = #{custNo}
			      ) a JOIN equip_mngr_hstr b ON a.mngr_hstr_no = b.mngr_hstr_no
			          JOIN spot_equip C ON b.spot_equip_no = c.spot_equip_no
			          JOIN spot_info d ON c.spot_no = d.spot_no
			) a

		,	( SELECT b.meas_val
		          ,d.spot_nm
			FROM ( SELECT MAX(a.mngr_hstr_no) AS mngr_hstr_no
                   FROM equip_mngr_hstr a JOIN spot_equip b ON a.spot_equip_no = b.spot_equip_no AND b.spot_no = #{Num1}
                                                           and a.cust_no = b.cust_no and b.used_yn ='Y'
                                          JOIN spot_info C ON b.spot_no = c.spot_no and a.cust_no = c.cusst_no and c.used_yn ='Y'
                   where a.cust_no = #{custNo}
			      ) a JOIN equip_mngr_hstr b ON a.mngr_hstr_no = b.mngr_hstr_no
			          JOIN spot_equip C ON b.spot_equip_no = c.spot_equip_no
			          JOIN spot_info d ON c.spot_no = d.spot_no
			) b

        /* (equipMapper.getOperCount)*/
        SELECT count(a.mngr_hstr_no)
        FROM equip_mngr_hstr a JOIN code_info b ON a.test_tp = b.code_no
							   JOIN oper_mast C ON a.oper_no = C.oper_no and a.cust_no = c.cust_no and c.used_yn = 'Y'
							   JOIN prod_info d ON c.prod_no = d.prod_no and a.cust_no = c.cust_no and d.used_yn ='Y'
		where a.cust_no = #{custNo}
		  and a.used_yn ='Y'
		  and a.oper_no = #{oper_no}

/* (equipMapper.getEquipReportList)*/ select a.equip_report_no , a.fe_yn , a.sus_yn , a.prod_yn , a.prod_fe_yn , a.prod_sus_yn , a.meas_dt , b.prod_nm from equip_report a join prod_info b on a.prod_no = b.prod_no and a.cust_no = b.cust_no and and b.used_yn = 'Y' where a.used_yn = 'Y' and a.cust_no = #{custNo} and to_char(a.reg_dt,'YYYY-MM-DD') = #{regDt}
and a.meas_dt between #{startHm} and #{endHm}
order by a.meas_dt desc, b.prod_nm

select (count(a.equip_report_no))
from (
    select a.equip_report_no
    , a.fe_yn
    , a.sus_yn
    , a.prod_yn
    , a.prod_fe_yn
    , a.prod_sus_yn
    , a.meas_dt
    , b.prod_nm
    from equip_report a join prod_info b on a.prod_no = b.prod_no and a.cust_no = b.cust_no and b.used_yn = 'Y'
    where a.used_yn = 'Y'
      and a.cust_no = #{custNo}
    and to_char(a.reg_dt,'YYYY-MM-DD') = #{regDt}

        and a.meas_dt between #{startHm} and #{endHm}

) a
