mapper.select:
/* [설비 이력관리 - 그리드 페이징 카운트] pre_rec_mgmt.p0100.gridPagingCnt */
SELECT COUNT(*) AS CNT
FROM SHC_REC_PRE_LIST
WHERE 1=1
  AND L_CODE = #{recLargeCode}
  AND CREATE_DT BETWEEN STR_TO_DATE(#{thisYearFirst}, '%Y-%m-%d %H:%i:%s') AND STR_TO_DATE(CONCAT(#{thisYearLast}, " 23:59:59"), '%Y-%m-%d %H:%i:%s')

mapper.update:
/* [설비 이력관리 - 선행요건일지별개선조치 업데이트] pre_rec_mgmt.p0100.updatePreImprv */
UPDATE SHC_REC_PRE_IMPRV SET
                             IMPRV_COMM = #{imprvComm},
                             MODIFY_ID = #{userId},
                             MODIFY_DT = STR_TO_DATE(NOW(), '%Y-%m-%d %H:%i:%s')
WHERE REC_NO = #{recNo}

mapper.delete:
/* [설비 이력관리 - 선행요건일지별개선조치 삭제] pre_rec_mgmt.p0100.deletePreImprv */
DELETE FROM SHC_REC_PRE_IMPRV
WHERE REC_NO = #{recNo}
