mapper.select:
/*미홍 주문목록(ord18Mapper.getOrd18List) */ SELECT a.ord_mh_no , a.ord_no --주문번호 , a.ord_cust_nm --주문고객명 , a.ord_cust_cell_no --주문고객전화번호 , a.rcv_nm --수령고객명 , a.rcv_cell_no --수령고객전화번호 , a.rcv_Hr --수령시간 , a.rcv_mi --수령분 , a.rcv_addr --수령고객주소 , a.pkg_tp --포장벙법코드 , a.rcv_tp --수령방법코드 , a.fill_rmk --필링 , a.sheet_rmk --시트 , a.txt_rmk --문구 , a.note_rmk --비고 , b.ord_sts --상태 , to_char(b.ord_dt,'YYYY-MM-DD') as ord_dt --주문일자 , to_char(b.dlv_req_dt,'YYYY-MM-DD') as dlv_req_dt --발송예정일자 , to_char(b.dlv_dt,'YYYY-MM-DD') as dlv_req_dt --발송일자 , fn_get_code_name('nm',a.pkg_tp) as pkg_tp_nm --포장방법명 , fn_get_code_name('nm',a.rcv_tp) as rcv_tp_nm --수령방법명 , c.prod_no --품번 , fn_get_prod_name(c.prod_no) as prod_nm --품명 from ord_info18 a join ord_info b on a.ord_no = b.ord_no and a.cust_no = b.cust_no and b.used_yn = 'Y'
and b.ord_sts = #{ordSts}
join ord_prod c on b.ord_no = c.ord_no and a.cust_no = c.cust_no and c.used_yn = 'Y' where a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.cust_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
ORDER BY b.ord_dt desc, b.ord_nm LIMIT #{pageSz} OFFSET #{pageNo}

select count(a.ord_mh_no)
from (
        SELECT a.ord_mh_no
        from ord_info18 a join ord_info b on a.ord_no = b.ord_no
                                         and a.cust_no = b.cust_no
                                         and b.used_yn = 'Y'

                                            and b.ord_sts = #{ordSts}

        where a.used_yn = 'Y'
        and a.cust_no = #{custNo}


                AND upper(replace(a.cust_nm,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'



            and to_char(b.ord_dt,'YYYY-MM-DD') between substring(#{dateFr},1,10) and substring(#{dateTo},1,10)

     ) a
