mapper.select:
/* (입고처리 검수항목) qcMapper.getMatrIwhChkList */
SELECT a.chk_no --점검관리번호
     , a.chk_tp --점검유형
     , b.code_nm AS chk_tp_nm --점검유형명
     , a.chk_mth --점검방법
     , c.code_nm AS chk_mth_nm --점검방법명
     , COALESCE(a.chk_cont, c.code_nm) AS chk_cont
FROM matr_iwh_chk a JOIN code_info b ON a.chk_tp = b.code_no and b.cust_no = #{custNo}
                    JOIN code_info C ON a.chk_mth = c.code_no and c.cust_no = #{custNo}
WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}


        AND upper(replace(a.chk_cont,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'


limit #{pageSz} offset #{pageNo}

select count(a.chk_no)
from ( SELECT a.chk_no --점검관리번호
        FROM matr_iwh_chk a JOIN code_info b ON a.chk_tp = b.code_no and b.cust_no = #{custNo}
                            JOIN code_info C ON a.chk_mth = c.code_no and c.cust_no = #{custNo}
        WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}


                AND upper(replace(a.chk_cont,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'


) a

SELECT a.doc_no --입고검수문서관리번호 , a.doc_nm --입고검수문서명 , a.chk_yn --사용여부 FROM matr_iwh_doc a WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.doc_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
order by chk_yn desc , doc_no

/* (입고처리 검수항목) qcMapper.getMatrIwhChkList */
SELECT a.chk_no --점검관리번호
, a.chk_tp --점검유형
, b.code_nm AS chk_tp_nm --점검유형명
, a.chk_mth --점검방법
, c.code_nm AS chk_mth_nm --점검방법명
, COALESCE(a.chk_cont, c.code_nm) AS chk_cont
FROM matr_iwh_chk a JOIN code_info b ON a.chk_tp = b.code_no and b.cust_no = #{custNo}
JOIN code_info C ON a.chk_mth = c.code_no and c.cust_no = #{custNo}
WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}


        AND upper(replace(a.chk_cont,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'


limit #{pageSz} offset #{pageNo}

select count(a.chk_no)
from ( SELECT a.chk_no --점검관리번호
FROM prod_owh_chk a JOIN code_info b ON a.chk_tp = b.code_no and b.cust_no = #{custNo}
                    JOIN code_info C ON a.chk_mth = c.code_no and c.cust_no = #{custNo}
WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}


        AND upper(replace(a.chk_cont,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'


) a

SELECT a.doc_no --입고검수문서관리번호 , a.doc_nm --입고검수문서명 , a.chk_yn --사용여부 FROM prod_owh_doc a WHERE a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(a.doc_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
order by chk_yn desc , doc_no
