mapper.select:
/*창고목록 (stockMapper.getWhList) */ select a.wh_no --창고번호 , a.wh_nm --창고명 , a.wh_tp --창고유형코드 , concat(a.wh_loc_seq::varchar, '번 창고') as wh_loc_seq --창고번호 , a.save_tmpr --보관온도 , coalesce(a.max_row,0) as max_row , coalesce(a.max_col,0) as max_col , coalesce(a.max_stair,0) as max_stair , fn_get_code_name('nm',a.save_tmpr )as save_tmpr_nm , fn_get_code_name('nm',a.wh_tp )as wh_tp_nm from wh_info a where a.used_yn = 'Y' and a.cust_no = #{custNo}
and a.wh_tp = #{whTp}
and a.save_tmpr = #{saveTmpr}
and upper(replace(a.wh_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

select count(a.wh_no)
from ( select a.wh_no
        from wh_info a
        where a.used_yn = 'Y' and a.cust_no = #{custNo}

            and a.wh_tp = #{whTp}


            and a.save_tmpr = #{saveTmpr}



                and  upper(replace(a.wh_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'


) a

/*원자재 적재위치 (stockMapper.getMatrPosList)*/ select a.matr_pos_no --적재관리번호 , a.matr_no --자재번호 , a.wh_no --적재창고번호 , a.col_idx --적재위치(열) , a.row_idx --적재위치(행) , a.stair_idx --적재우치(단) , a.bar_code_img --바코드이미지 , a.qr_code_img --QR코드이미지 , to_char(a.mod_dt,'YYYY-MM-DD') as stk_dt --적재일자 , b.wh_nm --창고명 , e.matr_nm --원자재명 , a.iwh_seq , f.code_nm as matr_stat_nm --원자재상태(구매일고,생산투입,폐기,분실등) from matr_pos a join matr_info e on a.matr_no = e.matr_no and e.cust_no = #{custNo} join code_info f on a.matr_stat = f.code_no left join wh_info b on a.wh_no = b.wh_no and b.cust_no = #{custNo} where a.used_yn = 'Y' and a.cust_no = #{custNo}
and not a.col_idx = 0 and not a.row_idx = 0 and not a.stair_idx = 0
and a.col_idx = 0 and a.row_idx = 0 and a.stair_idx = 0
and a.purs_matr_no = #{pursMatrNo}
and a.matr_no = #{matrNo}
and a.wh_no = #{whNo}
ORDER BY a.matr_pos_no desc limit #{pageSz} offset #{pageNo}

select count(a.matr_no)
from (  select a.matr_no --자재번호
        from matr_pos a join matr_info e on a.matr_no = e.matr_no and e.cust_no = #{custNo}
                        join code_info f on a.matr_stat  = f.code_no
                   left join wh_info b on a.wh_no = b.wh_no and b.cust_no = #{custNo}
        where a.used_yn  = 'Y' and a.cust_no = #{custNo}

            and not a.col_idx = 0
            and not a.row_idx = 0
            and not a.stair_idx = 0



            and a.col_idx = 0
            and a.row_idx = 0
            and a.stair_idx = 0


            and a.purs_matr_no = #{pursMatrNo}



            and a.matr_no = #{matrNo}



            and a.wh_no = #{whNo}

) a

select a.matr_stk_no --적재관리번호
     , a.matr_no --자재번호
     , a.wh_no --창고번호
     , to_char(a.mod_dt, 'YYYY-MM-DD') as stk_dt -- 적재일자
     , b.matr_nm -- 자재명
     , b.item_cd  -- 자재코드
     , b.valid_term  --유효기간
     , fn_get_code_name('nm', b.matr_tp)  as matr_tp_nm
     , coalesce (a.stk_qty,0) as stk_qty  -- 재고량
from matr_stk a join matr_info b on a.matr_no = b.matr_no and b.used_yn = 'Y' and b.cust_no = #{custNo}
where a.used_yn = 'Y' and a.cust_no = #{custNo}

    and a.wh_no = #{whNo}

limit #{pageSz} offset #{pageNo}

select count(a.matr_stk_no)
from (
        select a.matr_stk_no --적재관리번호
        from matr_stk a join matr_info b on a.matr_no = b.matr_no and b.used_yn = 'Y' and b.cust_no = #{custNo}
        where a.used_yn = 'Y' and a.cust_no = #{custNo}

            and a.wh_no = #{whNo}

) a

select coalesce (b.iwh_qty,0) - coalesce (c.owh_qty,0)::numeric(10,3)
from matr_info a  left join ( select a.matr_no, sum(a.iwh_qty) as iwh_qty
                              from matr_iwh a
                              where wh_no = #{whNo} and a.cust_no = #{custNo}
                              and used_yn = 'Y'
                              group by a.matr_no
                  ) b on a.matr_no = b.matr_no
                  left join ( select a.matr_no, sum(a.owh_qty) as owh_qty
                              from matr_owh a
                              where wh_no = #{whNo} and a.cust_no = #{custNo}
                              and used_yn = 'Y'
                              group by a.matr_no
                  ) c on a.matr_no = c.matr_no
where  a.matr_no = #{matrNo} and a.cust_no = #{custNo}

mapper.update:
update iwh_stk set stk_qty = coalesce(stk_qty,0) + coalesce (#{stkQty},0)
                 , stat_cd = #{stat_cd}
                 , stat_trf_dt = now()
where matr_no = #{matrNo}
  and wh_no = #{whNo} and cust_no = #{custNo}

mapper.select:
update iwh_stk set stk_qty = coalesce(stk_qty,0) - coalesce (#{stkQty},0)
                 , stat_cd = #{stat_cd}
                 , stat_trf_dt = now()
where matr_no = #{matrNo}
  and wh_no = #{whNo} and cust_no = #{custNo}

/*원료재고목록 (stockMapper.getMatrRealStockList) */ select a.matr_no --자재번호 , a.matr_nm --자재명 , a.sz --자재규격 , a.vol --단위용량 --, b.chng_no --실사재고관리번 , c.matr_stk_no --현재고관리번호 , c.wh_no --창고번 , coalesce (c.stk_qty,0) as matr_stk --현재고(최종재고) , d.wh_nm , fn_get_code_name('nm',a.save_tmpr) as save_tmpr__nm --창고F --, b.chng_resn --변경사유코드 --, coalesce (b.stk_qty,0) as real_stk --실사재고 --, coalesce (b.used_yn,'Y') as used_yn --, z.code_alais as chng_resn_nm from matr_info a left join matr_stk c on a.matr_no = c.matr_no and c.used_yn = 'Y' and c.cust_no = #{custNo} --left join ( select a.* -- from matr_stk_hstr a -- where a.chng_no = (select max(chng_no) from matr_stk_hstr where matr_no = a.matr_no and used_yn = 'Y') -- and a.used_yn = 'Y' -- ) b on c.wh_no = b.wh_no and a.matr_no = b.matr_no left join wh_info d on c.wh_no = d.wh_no and d.cust_no = #{custNo} --left join code_info z on b.chng_resn = z.code_no where a.used_yn = 'Y' and a.cust_no = #{custNo}
and a.save_tmpr = #{saveTmpr}
and a.matr_tp = #{saveMatrTp}
and upper(replace(a.matr_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
order by matr_nm collate "ko_KR.utf8" limit #{pageSz} offset #{pageNo}

select count(a.matr_no)
from (
        select a.matr_no --자재번호
        from matr_info a left join matr_stk c on a.matr_no = c.matr_no and c.used_yn = 'Y' and c.cust_no = #{custNo}
                   --left join ( select a.*
                   --              from matr_stk_hstr a
                   --              where a.chng_no = (select max(chng_no) from matr_stk_hstr where matr_no = a.matr_no and used_yn = 'Y')
                   --              and a.used_yn = 'Y'
                   --            ) b on c.wh_no = b.wh_no and a.matr_no = b.matr_no
                    left join wh_info d on c.wh_no = d.wh_no and d.cust_no = #{custNo}
                    --left join code_info z on b.chng_resn = z.code_no
        where a.used_yn = 'Y' and a.cust_no = #{custNo}

            and a.save_tmpr = #{saveTmpr}


            and a.matr_tp = #{saveMatrTp}



                and  upper(replace(a.matr_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'


     ) a

select a.date as date , b.matr_no --지재번호 , b.matr_nm --자재명 , coalesce (b.yes_stk_qty,0) as matr_yes_stk_qty --자재전일재고 , coalesce (b.stk_qty,0) as matr_stk_qty --자재재고 , coalesce (b.iwh_qty,0) as matr_iwh_qty --자재입고 , coalesce (b.owh_qty,0) as matr_owh_qty --자재출고 from ( select to_char(generate_series(date(#{procYy}||'-'||#{procYm}||'-'||'01') , (date_trunc('month', (replace(#{procYy},'-','')||#{procYm}||'01')::date) + INTERVAL '1 MONTH - 1 day'), '1 day' ), 'YYYY-MM-DD') as date ) a left join (select to_char(a.clos_dt,'YYYY-MM-DD') as date , b.matr_no , b.matr_nm , coalesce (sum(a.stk_qty),0) as yes_stk_qty , coalesce (sum(a.iwh_qty),0) as iwh_qty , coalesce (sum(a.owh_qty),0) as owh_qty , coalesce (sum(c.stk_qty),0) as stk_qty from matr_stk_clos a join matr_info b on a.matr_no = b.matr_no and b.used_yn = 'Y' and b.cust_no = #{custNo} left join matr_stk c on a.matr_no = c.matr_no and c.used_yn = 'Y' and c.cust_no = #{custNo} where a.used_yn = 'Y' and a.cust_no = #{custNo} group by to_char(a.clos_dt,'YYYY-MM-DD') , b.matr_no, b.matr_nm ) b on a.date = b.date where 1 = 1 and coalesce(b.iwh_qty,0) + coalesce(b.owh_qty,0) > 0 and substring(a.date,1,7) = #{procYy}||'-'||#{procYm} order by date
AND upper(replace(b.matr_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

select count(a.date) from ( select a.date as date , b.matr_no --지재번호 , b.matr_nm --자재명 , coalesce (b.yes_stk_qty,0) as matr_yes_stk_qty --자재전일재고 , coalesce (b.stk_qty,0) as matr_stk_qty --자재재고 , coalesce (b.iwh_qty,0) as matr_iwh_qty --자재입고 , coalesce (b.owh_qty,0) as matr_owh_qty --자재출고 from ( select to_char(generate_series(date(#{procYy}||'-'||#{procYm}||'-'||'01') , (date_trunc('month', (replace(#{procYy},'-','')||#{procYm}||'01')::date) + INTERVAL '1 MONTH - 1 day'), '1 day' ), 'YYYY-MM-DD') as date ) a left join (select to_char(a.clos_dt,'YYYY-MM-DD') as date , b.matr_no , b.matr_nm , coalesce (sum(a.stk_qty),0) as yes_stk_qty , coalesce (sum(a.iwh_qty),0) as iwh_qty , coalesce (sum(a.owh_qty),0) as owh_qty , coalesce (sum(c.stk_qty),0) as stk_qty from matr_stk_clos a join matr_info b on a.matr_no = b.matr_no and b.used_yn = 'Y' and b.cust_no = #{custNo} left join matr_stk c on a.matr_no = c.matr_no and c.used_yn = 'Y' and c.cust_no = #{custNo} where a.used_yn = 'Y' and a.cust_no = #{custNo} group by to_char(a.clos_dt,'YYYY-MM-DD') , b.matr_no, b.matr_nm ) b on a.date = b.date where 1 = 1 and coalesce(b.iwh_qty,0) + coalesce(b.owh_qty,0) > 0 and substring(a.date,1,7) = #{procYy}||'-'||#{procYm}
AND upper(replace(b.matr_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
) a

select a.date as date , b.prod_no --품번 , b.prod_nm --품명 , coalesce (b.yes_stk_qty,0) as prod_yes_stk_qty --상품재고 , coalesce (b.stk_qty,0) as prod_stk_qty --상품재고 , coalesce (b.iwh_qty,0) as prod_iwh_qty --상품입고 , coalesce (b.owh_qty,0) as prod_owh_qty --상품출고 from ( select to_char(generate_series(date('2021'||'-'||#{procYm}||'-'||'01') , (date_trunc('month', (replace('2021','-','')||#{procYm}||'01')::date) + INTERVAL '1 MONTH - 1 day'), '1 day' ), 'YYYY-MM-DD') as date ) a left join (select to_char(a.clos_dt,'YYYY-MM-DD') as date , b.prod_no , b.prod_nm , coalesce (sum(a.stk_qty),0) as yes_stk_qty , coalesce (sum(a.iwh_qty),0) as iwh_qty , coalesce (sum(a.owh_qty),0) as owh_qty , coalesce (sum(c.stk_qty),0) as stk_qty from prod_stk_clos a join prod_info b on a.prod_no = b.prod_no and b.used_yn = 'Y' and b.cust_no = #{custNo} left join prod_stk c on a.prod_no = c.prod_no and c.used_yn = 'Y' and c.cust_no = #{custNo} where a.used_yn = 'Y' and a.cust_no = #{custNo} group by to_char(a.clos_dt,'YYYY-MM-DD'), b.prod_no,b.prod_nm ) b on a.date = b.date where 1 = 1 and coalesce(b.iwh_qty,0) + coalesce(b.owh_qty,0) > 0 and substring(a.date,1,7) = #{procYy}||'-'||#{procYm} order by date
AND upper(replace(b.prod_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
limit #{pageSz} offset #{pageNo}

select count(a.date)
from (  select a.date as date
from ( select  to_char(generate_series(date('2021'||'-'||#{procYm}||'-'||'01')
                , (date_trunc('month', (replace('2021','-','')||#{procYm}||'01')::date) + INTERVAL '1 MONTH - 1 day'), '1 day' ), 'YYYY-MM-DD') as date
     ) a left join ( select to_char(a.clos_dt,'YYYY-MM-DD') as date
                        , b.prod_no
                        , b.prod_nm
                        , coalesce (sum(a.stk_qty),0) as yes_stk_qty
                        , coalesce (sum(a.iwh_qty),0) as iwh_qty
                        , coalesce (sum(a.owh_qty),0) as owh_qty
                        , coalesce (sum(c.stk_qty),0) as stk_qty
                    from prod_stk_clos a join prod_info b on a.prod_no = b.prod_no and b.used_yn = 'Y' and b.cust_no = #{custNo}
                                    left join prod_stk c on a.prod_no = c.prod_no and c.used_yn = 'Y' and c.cust_no = #{custNo}
                    where a.used_yn = 'Y' and a.cust_no = #{custNo}
                    group by to_char(a.clos_dt,'YYYY-MM-DD'), b.prod_no,b.prod_nm
                ) c on  a.date = c.date
where 1 = 1
and coalesce(c.iwh_qty,0) + coalesce(c.owh_qty,0)  >  0
and substring(a.date,1,7) =  #{procYy}||'-'||#{procYm}


        AND upper(replace(c.prod_nm,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'


) a

select sum(matr_qty) as stk_qty
  from matr_pos
  where matr_no = #{matrNo} and wh_no = #{whNo} and matr_stat = #{matrStat} and used_yn = 'Y' and cust_no = #{custNo}

mapper.update:
update matr_stk
set stk_qty = (select sum(matr_qty)
               from matr_pos
               where matr_no = #{matrNo} and wh_no = #{whNo} and matr_stat = #{matrStat} and used_yn = 'Y' and cust_no = #{custNo}
               )
    , stat_trf_dt = now()
where wh_no = #{whNo} and matr_no = #{matrNo} and cust_no = #{custNo}

mapper.select:
select a.matr_no
     , b.bar_code_img
     , a.row_idx
     , a.col_idx
     , a.stair_idx
     , b.mess --단위중량
     , a.matr_qty --적재용량
     , 'http://'||#{serverIp} || ':' || #{portNo} || '/static/img/' || a.matr_qty::varchar || '.png' as bar_code_url
from matr_pos a join matr_info b on a.matr_no = b.matr_no and b.cust_no = #{custNo}
where a.matr_no = #{matrNo} and a.cust_no = #{custNo}
  and a.stair_idx  = #{stairIdx}
  and a.wh_no = #{whNo}

select a.stair_idx
     , a.col_idx
     , a.row_idx
     , sum(a.matr_qty) as stk_qty
     , a.matr_no
     , a.wh_no
     , c.max_stair
     , c.max_row
     , c.max_col
from matr_pos a join matr_stk b on a.matr_no = b.matr_no and b.cust_no = #{custNo}
                join wh_info c on a.wh_no = c.wh_no and c.cust_no = #{custNo}
where a.stair_idx * a.row_idx * a.col_idx != 0
  and a.cust_no = #{custNo}
  and a.matr_no = #{matr_no}
  and a.wh_no = #{wh_no}
group by a.stair_idx , a.col_idx , a.row_idx , a.matr_no, a.wh_no , c.max_stair , c.max_col , c.max_row

select count(a.matr_no)
from matr_pos a join matr_stk b on a.matr_no = b.matr_no and b.cust_no = #{custNo}
                join wh_info c on a.wh_no = c.wh_no and c.cust_no = #{custNo}
where a.stair_idx * a.row_idx * a.col_idx != 0
  and a.cust_no = #{custNo}
  and a.matr_no = #{matr_no}
  and a.wh_no = #{wh_no}
group by a.stair_idx , a.col_idx , a.row_idx , a.matr_no, a.wh_no , c.max_stair , c.max_col , c.max_row

mapper.update:
update matr_pos a set matr_qty = matr_qty - #{owh_qty}
where a.stair_idx = #{stair_idx}
  and a.row_idx = #{row_idx}
  and a.col_idx = #{col_idx}
  and a.matr_no = #{matr_no}
  and a.wh_no = #{wh_no}
  and a.cust_no = #{custNo}

update matr_stl a set stk_qty = stk_qty - #{owh_qty}
where a.matr_no = #{matr_no}
  and a.wh_no = #{wh_no}
  and a.cust_no = #{custNo}

mapper.select:
select count(iwh_no)
from matr_iwh
where matr_no = #{matrNo}
  and purs_no = #{pursNo}
  and purs_matr_no = #{pursMatrNo}
  and coalesce(retn_qty, 0) = 0
  and used_yn = 'Y'
  and cust_no = #{custNo}

/* 수불대장/자재(stockMapper.getMatrStkClosList) */ select a.wh_no --창고번호 , e.wh_nm --창고명 , a.matr_no --원자재번호 , b.matr_nm --원자재명 , g.matr_stk_clos_no --마감번호 , to_char(g.clos_dt,'YYYY-MM-DD') as clos_dt --마감일자 , floor (coalesce (d.stk_qty,0)) as jb_stk_qty --전일재고 , floor (coalesce (g.iwh_qty,0)) as iwh_qty --자재입고 , floor (coalesce (g.owh_qty,0)) as owh_qty --자재출고 , floor (coalesce (g.stk_qty,0)) as stk_qty --자재재고 , case when coalesce(f.iwh_qty,0) + coalesce (k.owh_qty,0) > 0 then '재고조정발생' when h.stk_qty is null then '전일자료이관' else '변경이력발생' --금일재고변경이력 end as stk_tp --재고발생사유 from matr_stk a join matr_info b on a.matr_no = b.matr_no and b.used_yn = 'Y' and b.cust_no = #{custNo} join wh_info e on a.wh_no = e.wh_no and e.used_yn = 'Y' and e.cust_no = #{custNo} left join matr_stk_clos g on a.wh_no = g.wh_no and a.matr_no = g.matr_no and g.used_yn = 'Y' and g.cust_no = #{custNo} and to_char(g.clos_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) left join ( select a.wh_no , a.matr_no , to_char(a.clos_dt,'YYYY-MM-DD') as clos_dt , coalesce (sum(stk_qty),0) as stk_qty from matr_stk_clos a where a.used_yn = 'Y' and to_char(clos_dt,'YYYY-MM-DD') = to_char(substring(#{closDt},1,10)::date + '-1 day'::interval,'YYYY-MM-DD') and a.cust_no = #{custNo} group by wh_no,matr_no,to_char(clos_dt,'YYYY-MM-DD') ) d on a.wh_no = d.wh_no and a.matr_no = d.matr_no left join ( select matr_no, wh_no, sum(iwh_qty) as iwh_qty from matr_iwh where to_char(iwh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,matr_no ) f on a.wh_no = f.wh_no and a.matr_no = f.matr_no left join ( select matr_no, wh_no, sum(owh_qty) as owh_qty from matr_owh where to_char(owh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,matr_no ) k on a.wh_no = k.wh_no and a.matr_no = k.matr_no left join ( select a.stk_qty , a.matr_no , a.wh_no from matr_stk_hstr a where chng_no = ( select max(k.chng_no) from matr_stk_hstr k where k.used_yn = 'Y' and to_char(coalesce (k.mod_dt,k.reg_dt),'YYYY-MM-DD') = substring(#{closDt},1,10) and a.matr_no = k.matr_no and a.wh_no = k.wh_no and k.cust_no = #{custNo} ) and a.cust_no = #{custNo} ) h on a.matr_no = h.matr_no and a.wh_no = h.wh_no where a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(b.matr_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
order by clos_dt, matr_nm collate "ko_KR.utf8" limit #{pageSz} offset #{pageNo}

select count(a.matr_stk_clos_no) from ( select g.matr_stk_clos_no from matr_stk a join matr_info b on a.matr_no = b.matr_no and b.used_yn = 'Y' and b.cust_no = #{custNo} join wh_info e on a.wh_no = e.wh_no and e.used_yn = 'Y' and e.cust_no = #{custNo} left join matr_stk_clos g on a.wh_no = g.wh_no and a.matr_no = g.matr_no and g.used_yn = 'Y' and g.cust_no = #{custNo} and to_char(g.clos_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) left join ( select a.wh_no , a.matr_no , to_char(a.clos_dt,'YYYY-MM-DD') as clos_dt , coalesce (sum(stk_qty),0) as stk_qty from matr_stk_clos a where a.used_yn = 'Y' and a.cust_no = #{custNo} and to_char(clos_dt,'YYYY-MM-DD') = to_char(substring(#{closDt},1,10)::date + '-1 day'::interval,'YYYY-MM-DD') and a.cust_no = #{custNo} group by wh_no,matr_no,to_char(clos_dt,'YYYY-MM-DD') ) d on a.wh_no = d.wh_no and a.matr_no = d.matr_no left join ( select matr_no, wh_no, sum(iwh_qty) as iwh_qty from matr_iwh where to_char(iwh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,matr_no ) f on a.wh_no = f.wh_no and a.matr_no = f.matr_no left join ( select matr_no, wh_no, sum(owh_qty) as owh_qty from matr_owh where to_char(owh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,matr_no ) k on a.wh_no = k.wh_no and a.matr_no = k.matr_no left join ( select a.stk_qty , a.matr_no , a.wh_no from matr_stk_hstr a where a.used_yn = 'Y' and chng_no = ( select max(k.chng_no) from matr_stk_hstr k where used_yn = 'Y' and to_char(coalesce (k.mod_dt,k.reg_dt),'YYYY-MM-DD') = substring(#{closDt},1,10) and a.matr_no = k.matr_no and a.wh_no = k.wh_no and k.cust_no = #{custNo} ) and a.cust_no = #{custNo} ) h on a.matr_no = h.matr_no and a.wh_no = h.wh_no where a.used_yn = 'Y' and a.cust_no = #{custNo}
AND upper(replace(b.matr_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%'
) a

/* 수불대장/제품(stockMapper.getProdStkClosList) */ select g.prod_stk_clos_no --마감번호 , to_char(g.clos_dt,'YYYY-MM-DD') as clos_dt --마감일자 , a.wh_no , f.wh_nm , a.prod_no , c.prod_nm --품목제조보고 품명 , c.erp_prod_nm --ERP 품명 , floor(coalesce (g.iwh_qty,0)) as iwh_qty --재품입고 , floor(coalesce (g.owh_qty,0)) as owh_qty --재품출고 , floor(coalesce (g.stk_qty,0)) as stk_qty --마감일자기준 재품최종재고 , floor(coalesce (d.stk_qty,0)) as jb_stk_qty --전일재고 , case when coalesce(e.iwh_qty,0) + coalesce (k.owh_qty,0) > 0 then '재고조정발생' when h.stk_qty is null then '전일자료이관' else '변경이력발생' --금일재고변경이력 end as stk_tp --재고발생사유 from prod_stk a join wh_info f on a.wh_no = f.wh_no and f.used_yn = 'Y' and f.cust_no = #{custNo} join prod_stk_clos g on a.wh_no = g.wh_no and a.prod_no = g.prod_no and g.cust_no = #{custNo} and to_char(g.clos_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) join prod_info c on a.prod_no = c.prod_no and c.cust_no = #{custNo} left join ( select a.wh_no , a.prod_no , to_char(a.clos_dt,'YYYY-MM-DD') as clos_dt , coalesce (sum(stk_qty),0) as stk_qty from prod_stk_clos a where to_char(clos_dt,'YYYY-MM-DD') = to_char(substring(#{closDt},1,10)::date + '-1 day'::interval,'YYYY-MM-DD') and a.cust_no = #{custNo} group by wh_no,prod_no,to_char(clos_dt,'YYYY-MM-DD') ) d on a.wh_no = d.wh_no and a.prod_no = d.prod_no left join ( select prod_no, wh_no, sum(iwh_qty) as iwh_qty from prod_iwh where to_char(iwh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,prod_no ) e on a.wh_no = e.wh_no and a.prod_no = e.prod_no left join ( select prod_no, wh_no, sum(owh_qty) as owh_qty from prod_owh where to_char(owh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,prod_no ) k on a.wh_no = k.wh_no and a.prod_no = k.prod_no left join ( select a.stk_qty , a.prod_no , a.wh_no from prod_stk_hstr a where chng_no = ( select max(k.chng_no) from prod_stk_hstr k where to_char(coalesce (k.mod_dt,k.reg_dt),'YYYY-MM-DD') = substring(#{closDt},1,10) and a.prod_no = k.prod_no and a.wh_no = k.wh_no and k.cust_no = #{custNo} ) ) h on a.prod_no = h.prod_no and a.wh_no = h.wh_no where 1 = 1 and a.cust_no = #{custNo}
AND ( upper(replace(c.prod_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%' or upper(replace(c.erp_prod_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%' )
order by prod_nm collate "ko_KR.utf8" ,clos_dt limit #{pageSz} offset #{pageNo}

