left join ( select prod_no, wh_no, sum(iwh_qty) as iwh_qty from prod_iwh where to_char(iwh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,prod_no ) e on a.wh_no = e.wh_no and a.prod_no = e.prod_no left join ( select prod_no, wh_no, sum(owh_qty) as owh_qty from prod_owh where to_char(owh_dt,'YYYY-MM-DD') = substring(#{closDt},1,10) and used_yn = 'Y' and cust_no = #{custNo} group by wh_no,prod_no ) k on a.wh_no = k.wh_no and a.prod_no = k.prod_no left join ( select a.stk_qty , a.prod_no , a.wh_no from prod_stk_hstr a where chng_no = ( select max(k.chng_no) from prod_stk_hstr k where to_char(coalesce (k.mod_dt,k.reg_dt),'YYYY-MM-DD') = substring(#{closDt},1,10) and a.prod_no = k.prod_no and a.wh_no = k.wh_no and k.cust_no = #{custNo} ) ) h on a.prod_no = h.prod_no and a.wh_no = h.wh_no where 1 = 1 and a.cust_no = #{custNo}
AND ( upper(replace(c.prod_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%' or upper(replace(c.erp_prod_nm,' ','')) like '%' || upper(replace(#{findSz}, ' ','')) || '%' )
) a

select count(a.stk_clos_no)
from (
       select a.stk_clos_no
       from stk_clos a  left join matr_info b on a.matr_no = b.matr_no and b.cust_no = #{custNo}
                        left join prod_info c on a.prod_no = c.prod_no and c.cust_no = #{custNo}

                        left join ( select matr_no,to_char(clos_dt,'YYYY-MM-DD') as clos_dt ,coalesce (sum(matr_stk_qty),0) as  matr_stk_qty
                                    from stk_clos
                                    where to_char(clos_dt,'YYYY-MM-DD') = to_char(now() - '1 day' ::interval,'YYYY-MM-DD') and cust_no = #{custNo}
                                    group by matr_no,to_char(clos_dt,'YYYY-MM-DD')
                        ) d on a.matr_no = d.matr_no

                        left join ( select prod_no,to_char(clos_dt,'YYYY-MM-DD') as clos_dt ,coalesce (sum(prod_stk_qty),0) as  prod_stk_qty
                                    from stk_clos
                                    where to_char(clos_dt,'YYYY-MM-DD') = to_char(clos_dt - '1 day' ::interval,'YYYY-MM-DD') and cust_no = #{custNo}
                                    group by prod_no,to_char(clos_dt,'YYYY-MM-DD')
                        ) e on a.prod_no = e.prod_no

       where to_char(a.clos_dt,'YYYY-MM-DD') between substring(#{dateFr},1,10) and substring(#{dateTo},1,10) and a.cust_no = #{custNo}


                AND upper(replace(b.matr_nm,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'


                AND (     upper(replace(c.prod_nm,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'
                      or upper(replace(c.erp_prod_nm,' ',''))  like '%' || upper(replace(#{findSz}, ' ','')) || '%'
                    )


) a

select a.matr_no --자재번호
     , a.matr_nm --자재명
     , b.iwh_no --입고번호
     , b.cmpy_no as purs_cmpy_no --구매처번호
     , b.purs_no --구매번호
     , f.ord_no --주문번호
     , d.cmpy_no --주문고객번호
     , g.cmpy_nm --주문고객명
     , h.prod_nm --주문상품명
     , b.iwh_qty --입고수량
     , to_char(d.ord_dt,'YYYY-MM-DD') as ord_dt --주문일자
     , to_char(c.purs_dt,'YYYY-MM-DD') as purs_dt --자재구매일자
     , to_char(b.iwh_dt,'YYYY-MM-DD') as iwh_dt --자재납품일자
from matr_info a join matr_iwh b on a.matr_no = b.matr_no and b.used_yn = 'Y'
                                and to_char(b.iwh_dt,'YYYY-MM-DD') = #{procDt} and b.cust_no = #{custNo}
                 join purs_info c on b.purs_no  = c.purs_no and c.cust_no = #{custNo}
            left join ord_info d on c.ord_no = d.ord_no  and d.cust_no = #{custNo}
            left join ord_prod e on d.ord_no = e.ord_no and e.used_yn  = 'Y' and e.cust_no = #{custNo}
            left join make_indc f on d.ord_no = f.ord_no and f.par_indc_no  = 0 and f.used_yn = 'Y' and f.cust_no = #{custNo}
            left join cmpy_info g on d.cmpy_no = g.cmpy_no and g.cust_no = #{custNo}
            left join prod_info h on e.prod_no = h.prod_no  and h.cust_no = #{custNo}
where 1 = 1 and a.cust_no = #{custNo}
 AND a.matr_no = #{matrNo} 
 AND e.prod_no = #{prodNo} 
order by matr_nm collate "ko_KR.utf8"
limit #{pageSz} offset #{pageNo}

