select count(a.matr_no)
from (
         select a.matr_no --자재번호
         from matr_info a join matr_owh b on a.matr_no = b.matr_no and b.used_yn = 'Y'
                                       and to_char(b.owh_dt,'YYYY-MM-DD') = '2021-04-16' and b.cust_no = #{custNo}
                        left join make_indc c on b.indc_no = c.indc_no and c.cust_no = #{custNo} and c.par_in dc_no  = 0
                        left join ord_info d on c.ord_no = d.ord_no and d.cust_no = #{custNo} --and d.used_yn  'Y'
                        left join ord_prod e on d.ord_no = e.ord_no  and e.cust_no = #{custNo} --and e.used_yn  = 'Y'
                        left join make_indc f on d.ord_no = f.ord_no and f.par_indc_no  = 0 and f.cust_no = #{custNo} --and f.used_yn = 'Y'
                        left join cmpy_info g on d.cmpy_no = g.cmpy_no and g.cust_no = #{custNo}
                        left join prod_info h on e.prod_no = h.prod_no and h.cust_no = #{custNo}
         where 1 = 1 and a.cust_no = #{custNo}
         AND a.matr_no = #{matrNo} 
         AND e.prod_no = #{prodNo} 
     ) a

select a.prod_no --품번
     , a.prod_nm --품명
     , coalesce (b.wh_no,0) as wh_no --창고번호
     , f.ord_no --주문번호
     , to_char(f.ord_dt,'YYYY-MM-DD') as ord_dt --주문일자
     , to_char(f.dlv_req_dt,'YYYY-MM-DD') as dlv_req_dt --납기요청일자
     , to_char(b.iwh_dt,'YYYY-MM-DD')  as iwh_dt --상품일고일
     , round(coalesce(b.iwh_qty,0)) as iwh_qty --입고수량
     , to_char(f.dlv_dt,'YYYY-MM-DD')  as dlv_dt --납품일자
     , g.cmpy_no --고객번호
     , g.cmpy_nm --고객명
from prod_info a join prod_iwh b on a.prod_no = b.prod_no and b.used_yn = 'Y'
    and to_char(b.iwh_dt,'YYYY-MM-DD') =  #{procDt} and b.cust_no = #{custNo}
                 join make_indc e on a.prod_no = e.prod_no and e.cust_no = #{custNo} and e.par_indc_no  = 0 --and e.proc_cd  = 999
                 left join ord_info  f on e.ord_no = f.ord_no and f.cust_no = #{custNo}
                 left join cmpy_info g on f.cmpy_no  = g.cmpy_no  and g.cust_no = #{custNo}
where a.used_yn =  'Y' and a.cust_no = #{custNo}
 AND a.prod_no = #{prodNo} 
order by iwh_dt desc , prod_nm collate "ko_KR.utf8", cmpy_nm collate "ko_KR.utf8"
limit #{pageSz} offset #{pageNo}

