                               and to_char(b.owh_dt,'YYYY-MM-DD') =  #{procDt} and b.cust_no = #{custNo}
                 join make_indc e on a.prod_no = e.prod_no and e.par_indc_no  = 0 and e.cust_no = #{custNo} --and e.proc_cd  = 999
