            left join cmpy_info g on f.cmpy_no  = g.cmpy_no and g.cust_no = #{custNo}
where a.used_yn =  'Y' and a.cust_no = #{custNo}
 AND a.prod_no = #{prodNo} 
order by dlv_req_dt desc , prod_nm collate "ko_KR.utf8", cmpy_nm collate "ko_KR.utf8"
limit #{pageSz} offset #{pageNo}

select count(a.prod_no)
from (
         select a.prod_no --품번
         from prod_info a join prod_owh b on a.prod_no = b.prod_no and b.used_yn = 'Y' and b.cust_no = #{custNo}
                                         and to_char(b.owh_dt,'YYYY-MM-DD') =  #{procDt}
                          join make_indc e on a.prod_no = e.prod_no and e.cust_no = #{custNo} and e.par_indc_no  = 0 --and e.proc_cd  = 999
                     left join ord_info  f on e.ord_no = f.ord_no and f.cust_no = #{custNo}
                     left join cmpy_info g on f.cmpy_no  = g.cmpy_no and g.cust_no = #{custNo}
         where a.used_yn =  'Y' and a.cust_no = #{custNo}
         AND a.prod_no = #{prodNo} 
) a

select a.matr_no --자재번호
     , a.matr_nm --자재명
     , coalesce (c.stk_qty,0) as stk_qty --현재고
from matr_info a join matr_stk c on a.matr_no = c.matr_no and c.used_yn = 'Y' and c.cust_no = #{custNo}
where a.used_yn = 'Y' and a.cust_no = #{custNo}


        and upper(a.matr_nm) like '%' || upper(#{findSz}) || '%'


        and a.matr_nm like '%' || #{findSz} || '%'


        AND fn_get_consonant(a.matr_nm)  = #{findSz}


order by matr_nm collate "ko_KR.utf8"

select count(a.matr_no)
  from (
            select a.matr_no --자재번호
            from matr_info a join matr_stk c on a.matr_no = c.matr_no and c.used_yn = 'Y' and c.cust_no = #{custNo}
            where a.used_yn = 'Y' and a.cust_no = #{custNo}


                    and upper(a.matr_nm) like '%' || upper(#{findSz}) || '%'


                    and a.matr_nm like '%' || #{findSz} || '%'


                    AND fn_get_consonant(a.matr_nm)  = #{findSz}


            order by matr_nm collate "ko_KR.utf8"
        ) a

select a.prod_no --자재번호
     , a.prod_nm --자재명
     , coalesce (c.stk_qty,0) as stk_qty --현재고
from prod_info a join prod_stk c on a.prod_no = c.prod_no and c.used_yn = 'Y' and c.cust_no = #{custNo}
where a.used_yn = 'Y' and a.cust_no = #{custNo}


        and upper(a.prod_nm) like '%' || upper(#{findSz}) || '%'


        and a.prod_nm like '%' || #{findSz} || '%'


        AND fn_get_consonant(a.prod_nm )  = #{findSz}


order by prod_nm collate "ko_KR.utf8"

select count(a.prod_no)
from (
        select a.prod_no --자재번호
        from prod_info a join prod_stk c on a.prod_no = c.prod_no and c.used_yn = 'Y' and c.cust_no = #{custNo}
        where a.used_yn = 'Y' and a.cust_no = #{custNo}


                and upper(a.prod_nm) like '%' || upper(#{findSz}) || '%'


                and a.prod_nm like '%' || #{findSz} || '%'


                AND fn_get_consonant(a.prod_nm )  = #{findSz}


        order by prod_nm collate "ko_KR.utf8"
) a

/* 원자재실사재고변경이력 (stockMapper.getMatrRealStockHstr) */
select a.chng_no --변경이력번호
     , a.matr_no --자재번호
     , d.matr_nm --자재명
     , to_char(coalesce(a.mod_dt,a.reg_dt),'YYYY-MM-DD HH24:MI:SS') as chng_dt --변경일자
     , a.stk_qty --변경재고
     , a.chng_resn --변경사유
     , a.wh_no --창고번호
     , b.code_nm as chng_resn_nm --변경사유명
     , c.wh_nm --창고명
from matr_stk_hstr a join code_info b on a.chng_resn = b.code_no
                     join wh_info c on a.wh_no = c.wh_no and c.cust_no = #{custNo}
                     join matr_info d on a.matr_no = d.matr_no and d.cust_no = #{custNo}
where a.used_yn = 'Y' and a.cust_no = #{custNo}
  and a.matr_no = #{matrNo}
order by chng_dt desc
limit #{pageSz} offset #{pageNo}

select count(a.chng_no)
from (
         select a.chng_no
         from matr_stk_hstr a join code_info b on a.chng_resn = b.code_no
                              join wh_info c on a.wh_no = c.wh_no and c.cust_no = #{custNo}
                              join matr_info d on a.matr_no = d.matr_no and d.cust_no = #{custNo}
         where a.used_yn = 'Y' and a.cust_no = #{custNo}
           and a.matr_no = #{matrNo}

     ) a

/* 원자재실사재고변경이력 (stockMapper.getProdRealStockHstr) */
select a.chng_no --변경이력번호
     , a.prod_no --자재번호
     , d.prod_nm --자재명
     , to_char(coalesce(a.mod_dt,a.reg_dt),'YYYY-MM-DD HH24:MI:SS') as chng_dt --변경일자
     , a.stk_qty --변경재고
     , a.chng_resn --변경사유
     , a.wh_no --창고번호
     , b.code_nm as chng_resn_nm --변경사유명
     , c.wh_nm --창고명
from prod_stk_hstr a join code_info b on a.chng_resn = b.code_no
                     join wh_info c on a.wh_no = c.wh_no and c.cust_no = #{custNo}
                     join prod_info d on a.prod_no = d.prod_no and d.cust_no = #{custNo}
where a.used_yn = 'Y' and a.cust_no = #{custNo}
  and a.prod_no = #{prodNo}
order by chng_dt desc
    limit #{pageSz} offset #{pageNo}

select count(a.chng_no)
from (
         select a.chng_no
         from prod_stk_hstr a join code_info b on a.chng_resn = b.code_no
                              join wh_info c on a.wh_no = c.wh_no and c.cust_no = #{custNo}
                              join prod_info d on a.prod_no = d.prod_no and d.cust_no = #{custNo}
         where a.used_yn = 'Y' and a.cust_no = #{custNo}
           and a.prod_no = #{prodNo}

     ) a

/*주문접수를 기반한 SHD 목록 (ioMapper.getMatrOwhHistList) */
select e.*
from (
    select a.date
        , coalesce (b.ord_recv_no,0) as ord_recv_no --수신관리번호
        , coalesce(b.cmpy_no ,0)  as cmpy_no --발송처번호
        , coalesce(b.prod_no ,0) as prod_no --요청제품번호
        , round(coalesce(b.req_qty,0)) as req_qty --요청수량
        , round(coalesce(c.stk_qty,0)) as stk_qty --재고수량
    from ( select to_char(DATE(GENERATE_SERIES(DATE  (substring(#{procDt},1,7) || '-01')
                , (date_trunc('MONTH', (substring(replace(#{procDt},'-',''),1,6)||'01')::date) + INTERVAL '1 MONTH - 1 day'),'1 day')),'YYYY-MM-DD') as date
         ) a
        left join ord_recv b on a.date = to_char(to_timestamp(b.req_ut),'YYYY-MM-DD')
                            and b.used_yn = 'Y'
                            and b.cust_no = #{custNo}
                             and b.prod_no = #{prodNo} 
        left join prod_stk c on b.cust_no = c.cust_no and b.prod_no = c.prod_no
                            and c.used_yn  = 'Y'
                             and c.prod_no = #{prodNo} 
    ) e
    where e.date between substring(#{procDt},1,10) and to_char(to_date(substring(#{procDt},1,10),'YYYY-MM-DD')  + '10 day'::interval,'YYYY-MM-DD')
order by e.date

/*주문접수를 기반한 생산계획 목록 (ioMapper.getShdPlanList) */
select e.*
from (
         select a.date
              , coalesce (b.plan_qty,0) as plan_qty
         from (select to_char(DATE(GENERATE_SERIES(DATE  (substring(#{procDt},1,7) || '-01')
                , (date_trunc('MONTH', (substring(replace(#{procDt},'-',''),1,6)||'01')::date) + INTERVAL '1 MONTH - 1 day'),'1 day')),'YYYY-MM-DD') as date
              ) a
             left join make_plan b on  a.date = to_char(to_timestamp(b.plan_ut),'YYYY-MM-DD')
                                  and b.cust_no = #{custNo}
                                  and b.prod_no = #{prodNo}
                                  and b.used_yn = 'Y'
     ) e
where e.date between substring(#{procDt},1,10) and to_char(to_date(substring(#{procDt},1,10),'YYYY-MM-DD')  + '10 day'::interval,'YYYY-MM-DD')
order by e.date
